process.env.NODE_ENV = 'test';
const request = require('supertest');
const { expect } = require('chai');
const app = require('../src/index');

const jwt = require('jsonwebtoken');

let newAccTokenHeader = '';

const expectedResponses = [
  {
    id: undefined,
    userId: 1,
    name: 'Bela',
    experience: 1,
    level: 1,
    healthmax: 1,
    healthact: 1,
    attackmin: 1,
    attackmax: 1,
    defense: 1,
    inventory: [],
    finalWords: null,
    smallImage: 'data:image/png;base64,', // eslint-disable-line
    bigImage: 'data:image/png;base64,', // eslint-disable-line
  },
  {
    Message: 'Sorry, this name is already in use!',
  },
  {
    error: 'Please provide a username and a userId',
  },
];

describe('POST /login', () => {
  it('should get back the userId from the accessToken and the header for POST /hero tests', (done) => {
    request(app)
      .post('/login')
      .send({
        username: 'Daniel',
        password: 'xxxx5555',
      })
      .end((err, data) => {
        if (err) return done(err);
        newAccTokenHeader = `Bearer ${data.body.accessToken}`;
        expect(JSON.stringify(jwt.decode(data.body.accessToken).userId)).to.equal(JSON.stringify(1));
        return done();
      });
  });
});

describe('POST /hero', () => {
  it('should respond with an object detailing the newly created hero', (done) => {
    request(app)
      .post('/hero')
      .set('Accept', 'application/json')
      .set('Authorization', newAccTokenHeader)
      .send({ name: 'Bela' })
      .expect('Content-Type', /json/)
      .expect(200)
      .end((err, data) => {
        if (err) return done(err);
        expect(JSON.stringify(data.body)).to.equal(JSON.stringify(expectedResponses[0]));
        return done();
      });
  });
  it('should respond with a message if the hero name already exists', (done) => {
    request(app)
      .post('/hero')
      .set('Accept', 'application/json')
      .set('Authorization', newAccTokenHeader)
      .send({ name: 'hero1' })
      .expect(200)
      .expect('Content-Type', /json/)
      .end((err, data) => {
        if (err) return done(err);
        expect(JSON.stringify(data.body)).to.equal(JSON.stringify(expectedResponses[1]));
        return done();
      });
  });
  it('should respond with a message if hero name is missing', (done) => {
    request(app)
      .post('/hero')
      .set('Accept', 'application/json')
      .set('Authorization', newAccTokenHeader)
      .expect(400)
      .expect('Content-Type', /json/)
      .end((err, data) => {
        if (err) return done(err);
        expect(JSON.stringify(data.body)).to.equal(JSON.stringify(expectedResponses[2]));
        return done();
      });
  });
});
